import React, { Component } from "react";

import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store.js";
import { Link } from 'react-router-dom';
import reactLogo from "../images/logo.svg";
import loginPict from "../images/loginpict.jpg";

class SignIn extends Component {
  doLogin = () => {
    const self = this;
    this.props.postLogin().then(() => {
      console.log("this", this);
      self.props.history.replace("./profile");
    });
  };

  render() {
    console.log("signin", this.props);
    return (
      <section className="content signin">
        <form onSubmit={e => e.preventDefault()}>
          <div fontSignHead style={{ color: "black" }}>
            <img
              src={loginPict}
              style={{ width: "100%", marginTop: "70px", height: "175px" }}
              alt=""
            />
            <div className="headerForm">
              <div className="container-fluid">
                <div className="row">
                  <div className="col-md-12 col-sm-12 offset-md-5">
                    <div id="myLogoBlack">
                      <p>WAWE</p>
                      <p style={{ fontSize: "30px" }}>WArunggaWE</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-md-5 col-sm-6 offset-md-0" />
            <div className="col-md-2 col-sm-6 offset-md-0">
              <div style={{textAlign:"center"}}>
                <input
                  type="text"
                  name="username"
                  placeholder="Username"
                  onChange={e => this.props.setField(e)}
                />
              </div>
              <div style={{textAlign:"center"}}>
                <input
                  type="password"
                  name="password"
                  placeholder="Password"
                  onChange={e => this.props.setField(e)}
                />
              </div>
              <div className="buttonSign" style={{textAlign:"center"}}>
                <button onClick={() => this.doLogin()}>Sign in</button>
                <p>
                  Belum punya akun?
                  <Link to={"/" + "daftar"}>
                    <p>Daftar disini!</p>
                  </Link>
                </p>
              </div>
            </div>
          </div>
        </form>
      </section>
    );
  }
}

export default connect(
  "is_login",
  "username",
  "pass",
  actions
)(withRouter(SignIn));
