import createStore from 'unistore';
import axios from 'axios';

const initialState = {
    pembeli_token:'',
    pembeli_username:'',
    pembeli_password:'',
    pembeli_avatar:'',
    is_login : false
}

const myReqUrl = "http://127.0.0.1:5000/pembeli/login";

export const store = createStore(initialState)

export const actions = store => ({
    setField:(state,e) => {
        return {[e.target.name] : e.target.value};
    },

    postLogout: state =>{
        return {is_login : false};
    },

    postLogin : async state => {
        const data = {username: state.pembeli_username, password: state.pembeli_password, token: state.pembeli_token, avatar:state.pembeli_avatar};
        // alert("post login")
        await axios
        .post("http://127.0.0.1:5000/pembeli/login")
        .then(response => {
            console.log("postLogin response", response.data);
                store.setState({
                    is_login : true,
                    username : response.data.user_data.username,
                    email : response.data.user_data.email,
                    avatar : response.data.user_data.avatar
                });
        }) 
        .catch(error => {
            console.log(error);
        })
    }
});
