import React, { Component } from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";

import "../styles/App.css";
import "../styles/bootstrap.min.css";


  class ShowCart extends Component {
    constructor(props) {
      super(props);
    }
    render() {

  return (
    <div id="kotak2">
    <img
    src={this.props.gambar}
    width="25%"
    style={{ float: "left", paddingRight: "10px" }}
  />
  <h5 style={{ float: "left", paddingRight: "50px" }}>
    Nama Barang : {this.props.nama}
  </h5>
  <h5>Jumlah Barang : {this.props.jumlah} </h5>
  <h5 style={{ float: "left", paddingRight: "200px" }} />
  <h5>Harga Total per transaksi : {this.props.hargaBayar} </h5>

  <div className="centercart">
    <div className="input-group">
      <span className="input-group-btn">
        <button
          type="button"
          className="btn btn-default btn-number"
          disabled="disabled"
          data-type="minus"
          data-field="quant[1]"
        >
          <span className="glyphicon glyphicon-minus" />
        </button>
      </span>

      {/* <input type="text" name="quant[1]" className="form-control input-number" value="1" */}
      {/* min="1" max="100"/> */}

      <span className="input-group-btn">
        <button
          type="button"
          className="btn btn-default btn-number"
          data-type="plus"
          data-field="quant[1]"
        >
          <span className="glyphicon glyphicon-plus" />
        </button>
      </span>
    </div>
  </div>
  </div>
    );
  } 
  }
export default ShowCart;
