import React, { Component } from "react";
import { connect } from "unistore/react";
import { actions } from "../store.js";
import { withRouter } from "react-router-dom";
import { Redirect } from "react-router-dom";

import engineerCor from "../images/engineer.jpg";
import doctorCor from "../images/doctor.jpg";
import programmerCor from "../images/programmer.jpeg";
import smartFarmCor from "../images/smartFarm.jpg";
import chemCor from "../images/chem.jpg";
import kitchenCor from "../images/kitchen.jpg";
import photographyCor from "../images/photography.jpg";
import artistCor from "../images/artist.jpg";

import Carousel from "react-bootstrap/Carousel";

import "../styles/App.css";
import "../styles/bootstrap.min.css";

import axios from "axios";

import Details from './detail.js';
const myReqUrl = "https://api-todofancy.herokuapp.com/api/movies";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listNews: [],
      username: "",
      isLogin: false
    };
  }

  componentDidMount = () => {
    const self = this;

    axios
      .get(myReqUrl)
      .then(function(response) {
        var allData = response.data.movies.filter(
          allData => allData.Category === "fiction"
        );
        self.setState({ listNews: allData });
        console.log(response.data);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  render() {
    const { listNews, username, islogin } = this.state;
    return (
      <div id="container">
        <div class="container-fluid" style={{ padding: "0" }}>
          <div class="contentMargin">
            <div class="shadow-lg p-3 mb-5 bg-white rounded">
              <hr />
            </div>
            <Carousel>
              <Carousel.Item>
                <img
                  className="engineer"
                  src={engineerCor}
                  alt="First slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Engineer</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="doctor"
                  src={doctorCor}
                  alt="Second slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Doctor</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="programmer"
                  src={programmerCor}
                  alt="Third slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Programmer</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="farmer"
                  src={smartFarmCor}
                  alt="Fourth slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Farmer</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="Chemist"
                  src={chemCor}
                  alt="Fifth slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Chemist</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="chef"
                  src={kitchenCor}
                  alt="Sixth slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Chef</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="chef"
                  src={photographyCor}
                  alt="Sixth slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Photographer</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="chef"
                  src={artistCor}
                  alt="Sixth slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Artist</h3>
                </Carousel.Caption>
              </Carousel.Item>
            </Carousel>
          </div>
        </div>

        <div className="album background" id="level-0">
          <div className="headerCart">
            <div className="col-md-6 col-sm-12 offset-md-0">
              <div className="flex-container">
                {listNews.map((item, key) => {
                  return (
                    <Details
                      key={key}
                      title={item.Title}
                      year={item.Year}
                      category={item.Category}
                      poster={item.Poster}
                      synopsis={item.Synopsis}
                    />
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

// export default connect(
//     "is_login, email, full_name",
//     actions
// )(withRouter(Home));

export default Home;
