import createStore from 'unistore';
import axios from 'axios';

const initialState = {
    api_key:'',
    // email:'',
    pembeli_password:'',
    // full_name:'',
    pembeli_username:'',
    is_login : false,
    // listNews : []
}


export const store = createStore(initialState)

export const actions = store => ({
    setField:(state,e) => {
        return {[e.target.name] : e.target.value};
    },
    postLogout: state =>{
        return {is_login : false};
    },
    postLogin : async state => {

        const data = {pembeli_username: state.username, pembeli_password: state.password};
        await axios
        .post("http://127.0.0.1:5000/pembeli/login", data)
        .then(response => {
            console.log("postLogin response", response.data);

                store.setState({
                    is_login : true,
                    api_key : response.data.Token,
                    // full_name : response.data.full_name,
                    // email : response.data.email
                });

        })
        .catch(error => {
            console.log(error);
        })
    },

    // showHeadLine : async state => {
