import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store";
import ShowCart from "./ShowCart.js";
import { Form } from "antd";
const FormItem = Form.Item;



// const Cart = props => {

class Cart extends Component {
  constructor(props) {
    super(props);
    this.state = {
      gambar: "",
      nama: "",
      harga: "",
      jumlah: "",
      status: "",
      tanggal: "",
      hargaBayar: ""
  }
}


    componentDidMount = () => {
      const self = this;
    };


render() {
  if (!this.props.is_login) {
    return <Redirect to={{ pathname: "/signin" }} />;
  } else {
    return (
        <div className="album background" id="level-0">
        <div className="container-fluid">
          <div className="headerCart">
            <div className="row">
              <div className="col-md-6 col-sm-12 offset-md-0">
                <h1>Cart</h1>
                <h4>Periksa kembali belanja Anda.</h4>

                <div className="flex-container">
                  <div id="kotak1">
                    <h3>Detail Belanja</h3>
                  </div>

                  <div id="kotak2head">
                    <h5>{this.props.pembeli_namaLengkap}</h5>
                  </div>

                  {this.props.listTransaksi.map((item, key) => {
                    return (
                      <ShowCart
                        gambar={item.produk_gambar}
                        nama={item.produk_nama}
                        harga={item.produk_harga}
                        jumlah={item.produk_jumlahBeli}
                        status={item.produk_statusPromo}
                        tanggal={item.produk_tanggalBeli}
                        hargaBayar={item.produk_hargaBayar}
                      />
                    );
                  })}

                </div>
              </div>

              <div className="col-md-6 col-sm-12 offset-md-0">
                <h1>Ringkasan Belanja</h1>
                <h4>Cek kembali Total Harga dan Metoda Pembayaran</h4>
                <div className="flex-container">
                  <div id="kotak1">
                    <h3>Detail Belanja</h3>
                  </div>

                  {/*  ini cart tambah url */}
                  <div id="kotak2">
                    <h5 style={{ float: "left"}}>
                      Total Harga Barang: Rp. {this.props.cartTotalByr}
                    </h5>
                    <br></br>
                    <h5 style={{ float: "left"}}>
                      Total Qty Barang : {this.props.cartTotalQty} pcs(s)
                    </h5>

                  </div>
                  <div id="kotak3">
                  <Form onSubmit={this.handleSubmit} id="myBuy">

                  <FormItem>
                    <label>
                      Pembayaran:
                      <div className="select-arrow">
                        <select
                          name="this.state.pembayaran"
                          // onChange={this.handlepembayaranChange}
                          style={{ width: "100%" }}
                        >
                          <option></option>
                          <option>Credit Card</option>
                          <option>Bank Transfer</option>
                          <option>Internet Banking</option>
                          <option>E-Wallet</option>
                        </select>
                      </div>
                    </label>
                  </FormItem>
                  </Form>
                  <div>
                  <button id="myPay" type="submit" value="Pay">
                    <p>Bayar</p>
                  </button>
                  </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
};

}


export default connect(
  [
    "is_login",
    "api_key",
    "pembeli_email",
    "pembeli_username",
    "pembeli_password",
    "pembeli_namaLengkap",
    "pembeli_gender",
    "pembeli_lokasiKota",
    "pembeli_pekerjaan",
    "pembeli_avatar",
    "listTransaksi",
    "cartTotalQty",
    "cartTotalByr"
  ],
  actions
)(withRouter(Cart));
