import createStore from "unistore";
import axios from "axios";

const initialState = {
  api_key: "",
  pembeli_email: "",
  pembeli_password: "",
  pembeli_username: "",
  pembeli_namaLengkap: "",
  pembeli_gender: "",
  pembeli_lokasiKota: "",
  pembeli_pekerjaan: "",
  is_login: false
};

const myReqUrl = "http://127.0.0.1:5000/pembeli/login";
const myDataUrl = "http://127.0.0.1:5000/pembeli/medi";

export const store = createStore(initialState);

export const actions = store => ({
  setField: (state, e) => {
    return { [e.target.name]: e.target.value };
  },
  postLogout: state => {
    return { is_login: false };
  },
  postLogin: async state => {
    const data = {
      pembeli_username: state.pembeli_username,
      pembeli_password: state.pembeli_password
    };

    await axios
      .post(myReqUrl, data)
      .then(response => {
        console.log("postLogin response", response.data);
        store.setState({
          is_login: true,
          api_key: response.data.Token
        });
      })
      .catch(error => {
        console.log(error);
      });

