import React, { Component } from "react";
import { Link } from "react-router-dom";
import propTypes from "prop-types";

import "../styles/App.css";
import "../styles/bootstrap.min.css";

class DetailProduk extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className="row">
        <div class="col-md-4 col-sm-12 offset-md-0">
          <div class="card" style={{ width: "300px" }}>
            <img
              class="card-img-top"
              src={this.props.gambar}
              alt="Card image"
              style={{ width: "100%" }}
            />
            <div class="card-body">
              <h4 class="card-title">Agriculture Drone</h4>
              <div class="priceShow">
                <p
                  class="card-text"
                  style={{ float: "left", paddingRight: "20px" }}
                >
                  Rp. {this.props.hargaAwal}
                </p>
                <p
                  class="card-text"
                  style={{
                    textDecoration: "line-through",
                    color: "red",
                    display: "block"
                  }}
                >
                  Rp. {this.props.hargaAkhir}
                </p>
              </div>
              <p class="card-text" style={{ clear: "left" }}>
                Some example text some example text.
              </p>
              <a href="#" class="btn btn-primary stretched-link">
                Buy
              </a>
              <a href="#" class="btn btn-primary stretched-link">
                Rent
              </a>

              <div class="center">
                <div class="input-group">
                  <span class="input-group-btn">
                    <button
                      type="button"
                      class="btn btn-default btn-number"
                      disabled="disabled"
                      data-type="minus"
                      data-field="quant[1]"
                    >
                      <span class="glyphicon glyphicon-minus" />
                    </button>
                  </span>

                  <input
                    type="text"
                    name="quant[1]"
                    class="form-control input-number"
                    value="1"
                    min="1"
                    max="100"
                  />
                  <span class="input-group-btn">
                    <button
                      type="button"
                      class="btn btn-default btn-number"
                      data-type="plus"
                      data-field="quant[1]"
                    >
                      <span class="glyphicon glyphicon-plus" />
                    </button>
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div class="col-md-8 col-sm-6 col-12">
          <div id="learn-title">
            <h1>Nama Produk: {this.props.nama}</h1>
            <h1>Harga: Rp. {this.props.hargaAwal}</h1>
            <h1>Harga Akhir: Rp. {this.props.hargaAkhir}</h1>
            <h1>Stock sisa:{this.props.stok} </h1>
            <h1>Deskripsi lengkap:</h1>
            <h2>{this.props.deskripsi}</h2>
          </div>
        </div>
      </div>
    );
  }
}

// DetailProduk.propTypes = {
//   name : PropTypes.string,
//   startDate : PropTypes.string,
//   image : PropTypes.string,
//   ID : PropTypes.string
// }

export default DetailProduk;
