import React, { Component } from "react";
import { connect } from "unistore/react";
import { actions } from "../store.js";
import { withRouter } from "react-router-dom";
import { Redirect } from "react-router-dom";

import engineerCor from "../images/engineer.jpg";
import doctorCor from "../images/doctor.jpg";
import programmerCor from "../images/programmer.jpeg";
import smartFarmCor from "../images/smartFarm.jpg";
import chemCor from "../images/chem.jpg";
import kitchenCor from "../images/kitchen.jpg";
import photographyCor from "../images/photography.jpg";
import artistCor from "../images/artist.jpg";

import Carousel from "react-bootstrap/Carousel";

import "../styles/App.css";
import "../styles/bootstrap.min.css";

import axios from "axios";
import DetailHome from "./DetailHome.js";
const myReqUrl = "http://127.0.0.1:5000/pembeli/produk/Teknik?rp=50&p=1";
// const myReqUrl = "http://127.0.0.1:5000/pembeli/produkAll/rosa?rp=100&p=1";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listProduk: [],
      username: "",
      isLogin: false
    };
  }

  componentDidMount = () => {
    const self = this;

    axios
      .get(myReqUrl)
      .then(function(response) {
        var allData = response.data.Produk;
        // var allData = response.data.Produk.filter(
        //   allData => allData.produk_kategori === "Teknik"
        // );
        self.setState({ listProduk: allData });
        console.log(response.data);
      })
      .catch(function(error) {
        console.log(error);
      });
  };

  render() {
    //   alert(listProduk)
    const { listProduk, username, islogin } = this.state;

    return (
      <div id="container">
        <div className="container-fluid" style={{ padding: "0" }}>
          <div className="contentMargin">
            <div className="shadow-lg p-3 mb-5 bg-white rounded">
              <hr />
            </div>
            <Carousel>
              <Carousel.Item>
                <img
                  className="engineer"
                  src={engineerCor}
                  alt="First slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Engineer</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="doctor"
                  src={doctorCor}
                  alt="Second slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Doctor</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="programmer"
                  src={programmerCor}
                  alt="Third slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Programmer</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="farmer"
                  src={smartFarmCor}
                  alt="Fourth slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Farmer</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="Chemist"
                  src={chemCor}
                  alt="Fifth slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Chemist</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="chef"
                  src={kitchenCor}
                  alt="Sixth slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Chef</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="chef"
                  src={photographyCor}
                  alt="Sixth slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Photographer</h3>
                </Carousel.Caption>
              </Carousel.Item>

              <Carousel.Item>
                <img
                  className="chef"
                  src={artistCor}
                  alt="Sixth slide"
                  style={{ height: "200px", width: "100%" }}
                />
                <Carousel.Caption>
                  <h3 style={{ fontSize: "30px" }}>Artist</h3>
                </Carousel.Caption>
              </Carousel.Item>
            </Carousel>
          </div>
            <br></br>
          <div className="row">
            <div className="col-md-12 col-sm-12 offset-md-0">
              <div id="subtitleProduk">
                <h2>Produk</h2>
              </div>
            </div>
          </div>

          <div className="produkHome">
            <div className="container-fluid">
              <div className="row">
                {listProduk.map((item, key) => {

                  return (
                    <DetailHome
                      key={key}
                      nama={item.produk_nama}
                      hargaAwal={item.produk_harga}
                      deskripsi={item.produk_deskripsi}
                      gambar={item.produk_gambar}
                      stok={item.produk_stok}
                      hargaAkhir={item.produk_hargaDiskon}
                      tanggalPost={item.produk_tanggalPost}
                    />
                  );
                })}
              </div>
            </div>
          </div>

        </div>
      </div>
    );
  }
}

// export default connect(
//     "is_login, email, full_name",
//     actions
// )(withRouter(Home));

export default Home;
