import React, { Component } from "react";
import propTypes from "prop-types";

import "../styles/App.css";
import "../styles/bootstrap.min.css";

class DetailHome extends Component {
  constructor(props) {
    super(props);
  }

  componentDidMount() {
    const { path } = this.props;
    import(`${path}`)
      .then(module => this.setState({ module: module.default }))
}

  render() {
    return (
      <div className="col-md-4 col-sm-12 offset-md-0">
        <div className="card" style={{ width: "300px" }}>
          <a href="detail.html">
            <img
              className="card-img-top"
              src={this.props.gambar}
              alt="Card image"
              style={{ width: "100%" }}
            />
          </a>
          <div className="card-body">
            <h4 className="card-title">{this.props.nama}</h4>
            <div className="priceShow">
              <p
                className="card-text"
                style={{ float: "left", paddingRight: "20px" }}
              >
                {this.props.hargaAwal}
              </p>
              <p
                className="card-text"
                style={{
                  textDecoration: "line-through",
                  color: "red",
                  display: "block"
                }}
              >
                {this.props.hargaAkhir}
              </p>
            </div>
            <p className="card-text" style={{ clear: "left" }}>
              {this.props.deskripsi}
            </p>
            <a
              href="detail.html"
              className="btn btn-primary stretched-link"
              style={{ marginLeft: "25%" }}
            >
              See Details
            </a>
          </div>
        </div>
      </div>
    );
  }
}

export default DetailHome;
