import React, { Component } from "react";
import propTypes from "prop-types";

import "../styles/App.css";
import "../styles/bootstrap.min.css";

class DetailHome extends Component {

  render() {
  return (
    <div className="col-md-4 col-sm-12 offset-md-0">
      <div className="card" style={{ width: "300px" }}>
        <a href="detail.html">
          <img
            className="card-img-top"
            src={props.gambar}
            alt="Card image"
            style={{ width: "100%" }}
          />
        </a>
        <div className="card-body">
          <h4 className="card-title">{props.nama}</h4>
          <div className="priceShow">
            <p
              className="card-text"
              style={{ float: "left", paddingRight: "20px" }}
            >
              {props.hargaAwal}
            </p>
            <p
              className="card-text"
              style={{
                textDecoration: "line-through",
                color: "red",
                display: "block"
              }}
            >
              {props.hargaAkhir}
            </p>
          </div>
          <p className="card-text" style={{ clear: "left" }}>
            {props.deskripsi}
          </p>
          <a
            href="detail.html"
            className="btn btn-primary stretched-link"
            style={{ marginLeft: "25%" }}
          >
            See Details
          </a>
        </div>
      </div>

    </div>

  );
};
};

export default DetailHome;
